[System.Diagnostics.CodeAnalysis.SuppressMessage('PSAvoidUsingConvertToSecureStringWithPlainText', '')]
[CmdletBinding()]
param (
)
$moduleManifestName = 'PSBuildSecrets.psd1';
$moduleRootPath = "$PSScriptRoot\..\..\Source";
$moduleManifestPath = Join-Path -Path $moduleRootPath -ChildPath $moduleManifestName;
Remove-Module $moduleManifestPath -Verbose -ErrorAction SilentlyContinue;
Import-Module $moduleManifestPath -Verbose

Describe "Unit Tests: Azure AD Authentication get (oauth) bearer token" {
    InModuleScope "PSBuildSecrets" {
        It "Mock Call New-KeyVaultClientAuthorizationToken" {
            $c_id,$c_pwd = "lessgrossman",("1234567890" | ConvertTo-SecureString -AsPlainText -Force);
            $t_id = "d863ab39-0b6a-eb81-9dde-5eb25b46cc33"
            $mock_token = "eyJ0eXAiOiJKV1QiLCJhbGciOiJSUzI1NiIsIng1dCI6Ik5HVEZ2ZEstZnl0aEV1THdqcHdBSk9NOW4tQSJ9..JZw8jC0gptZxVC-7l5sFkdnJgP3_tRjeQEPgUn28XctVe3QqmheLZw7QVZDPCyGycDWBaqy7FLpSekET_BftDkewRhyHk9FW_KeEz0ch2c3i08NGNDbr6XYGVayNuSesYk5Aw_p3ICRlUV1bqEwk-Jkzs9EEkQg4hbefqJS6yS1HoV_2EsEhpd_wCQpxK89WPs3hLYZETRJtG5kvCCEOvSHXmDE6eTHGTnEgsIk--UlPe275Dvou4gEAwLofhLDQbMSjnlV5VLsjimNBVcSRFShoxmQwBJR_b2011Y5IuD6St5zPnzruBbZYkGNurQK63TJPWmRd3mbJsGM0mf3CUQ"
            
            Mock New-KeyVaultClientAuthorizationToken -MockWith { return $mock_token };
            New-KeyVaultClientAuthorizationToken -TenantId $t_id -ClientId $c_id -ClientSecret $c_pwd | should -be $mock_token; 
            $was_called_once = 1
            Assert-MockCalled New-KeyVaultClientAuthorizationToken $was_called_once;
        }
    }
}
